INSERT INTO `agent_article` VALUES ('15', 'VPS租赁服务协议', '2013-01-06 10:18:49', 'VPS租赁服务协议1111', '1', '&lt;div style=&amp;#34;&amp;quot;&amp;quot;&amp;quot;margin:0px;padding:0px;font-family:simsun;font-size:14px;line-height:24px;background-color:#ffffff;&amp;quot;&amp;quot;&amp;quot;&amp;#34;&gt;&lt;span style=&amp;#34;&amp;quot;&amp;quot;&amp;quot;margin:0px;padding:0px;font-size:12px;&amp;quot;&amp;quot;&amp;quot;&amp;#34;&gt; 尊敬的景安网络用户：感谢您选择郑州市景安计算机网络技术有限公司为您提供的VPS服务器 （以下统称“虚拟专用服务器”）服务。请您认真细致地阅读以下的服务条款。用户只有同意了下述服务条款才能正式进入在线虚拟专用服务器申请程序。&lt;/span&gt;&lt;/div&gt;&lt;div style=&amp;#34;&amp;quot;&amp;quot;&amp;quot;margin:0px;padding:0px;font-family:simsun;font-size:14px;line-height:24px;background-color:#ffffff;&amp;quot;&amp;quot;&amp;quot;&amp;#34;&gt;&lt;/div&gt;&lt;div style=&amp;#34;&amp;quot;&amp;quot;&amp;quot;margin:0px;padding:0px;font-family:simsun;font-size:14px;line-height:24px;background-color:#ffffff;text-indent:18px;&amp;quot;&amp;quot;&amp;quot;&amp;#34;&gt;&lt;span style=&amp;#34;&amp;quot;&amp;quot;&amp;quot;margin:0px;padding:0px;font-size:12px;&amp;quot;&amp;quot;&amp;quot;&amp;#34;&gt;本条款由景安互联网数据中心 (以下简称乙方)与向乙方申请服务的用户(以下简称甲方)签订。&lt;/span&gt;&lt;/div&gt;&lt;div style=&amp;#34;&amp;quot;&amp;quot;&amp;quot;margin:0px;padding:0px;font-family:simsun;font-size:14px;line-height:24px;background-color:#ffffff;text-indent:18px;&amp;quot;&amp;quot;&amp;quot;&amp;#34;&gt;&lt;/div&gt;&lt;div style=&amp;#34;&amp;quot;&amp;quot;&amp;quot;margin:0px;padding:0px;font-family:simsun;font-size:14px;line-height:24px;background-color:#ffffff;&amp;quot;&amp;quot;&amp;quot;&amp;#34;&gt;&lt;strong style=&amp;#34;&amp;quot;&amp;quot;&amp;quot;margin:0px;padding:0px;&amp;quot;&amp;quot;&amp;quot;&amp;#34;&gt;&lt;span style=&amp;#34;&amp;quot;&amp;quot;&amp;quot;margin:0px;padding:0px;font-size:12px;&amp;quot;&amp;quot;&amp;quot;&amp;#34;&gt;第一条 合同项目与定义&lt;/span&gt;&lt;/strong&gt;&lt;/div&gt;&lt;div style=&amp;#34;&amp;quot;&amp;quot;&amp;quot;margin:0px;padding:0px;font-f