INSERT INTO `agent_article` VALUES ('25', 'php一些程序访问报错处理方法', '2015-08-14 10:26:07', 'PHP', '1', '<p style=\"margin-bottom: 0px;padding: 0px;width: 744px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, arial, sans-serif, Helvetica, 宋体;font-size: 12px;line-height: 20px;white-space: normal\"><span style=\";padding: 0px;line-height: 24px;width: 768px;text-indent: 24px;font-family: &#39;Microsoft Yahei&#39;, 宋体;font-size: 15px\"><span style=\";padding: 0px;width: 768px\">php</span><span style=\";padding: 0px;width: 768px;font-family: 宋体\">一些程序访问报错，具体特征如下：</span><span style=\";padding: 0px;width: 768px;color: red\">HTTP/1.1 200 OK Connection: close Date</span><span style=\";padding: 0px;width: 768px\">: Wed, 26 May 2010 08:11:38 GMT Content-Type: text xml Server: Microsoft-IIS/6.0 X-Powered-By: PHP/5.2.8 X-Powered-By: ASP.NET &nbsp;&nbsp;</span></span></p><p style=\"margin-bottom: 0px;padding: 0px;width: 744px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, arial, sans-serif, Helvetica, 宋体;font-size: 12px;line-height: 20px;white-space: normal\"><span style=\";padding: 0px;line-height: 24px;width: 768px;text-indent: 24px;font-family: &#39;Microsoft Yahei&#39;, 宋体;font-size: 15px\"><span style=\";padding: 0px;width: 768px;font-family: 宋体\">解决办法：直接吧</span><span style=\";padding: 0px;width: 768px\">PHP</span><span style=\";padding: 0px;width: 768px;font-family: 宋体\">运行模式换成</span><span style=\";padding: 0px;width: 768px\">isapi</span><span style=\";padding: 0px;width: 768px;font-family: 宋体\">就可以了。</span></span></p><p style=\"margin-bottom: 0px;padding: 0px;width: 744px;color: rgb(51, 51, 51);font-family: &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, arial, sans-serif, Helvetica, 宋体;font-size: 12px;line-height: 20px;white-space: normal\"><span style=\";padding: 0px;line-height: 24px;width: 768px;text-indent: 24px;font-family: &#39;Microsoft Yahei&#39;, 宋体;font-size: 15px\"><span style=\";padding: 0px;width: 768px;font-family: 宋体\">独立主机和</span><span style=\";padding: 0px;width: 768px\">VPS</span><span style=\";padding: 0px;width: 768px;font-family: 宋体\">远程登录服务器，打开</span><span style=\";padding: 0px;width: 768px\">IIS</span><span style=\";padding: 0px;width: 768px;font-family: 宋体\">选择相应站点右键属性》主目录》配置》，把映射中的</span><span style=\";padding: 0px;width: 768px\">php</span><span style=\";padding: 0px;width: 768px;font-family: 宋体\">和</span><span style=\";padding: 0px;width: 768px\">php3</span><span style=\";padding: 0px;width: 768px;font-family: 宋体\">改成（</span><span style=\";padding: 0px;width: 768px\">PHP</span><span style=\";padding: 0px;width: 768px;font-family: 宋体\">安装目录</span><span style=\";padding: 0px;width: 768px\">\\php5isapi.dll</span><span style=\";padding: 0px;width: 768px;font-family: 宋体\">）点确定。</span></span></p><p><br/></p>', '0000-00-00 00:00:00', '1');
