INSERT INTO `agent_common_problem` VALUES ('3', '7', '访问网站三级域名时出现“该网站伪装成播放器更新传播木马”或其他危险提示', '<p style=\"margin-bottom: 0px; margin-left: 24px; padding: 0px; width: 744px; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, arial, sans-serif, Helvetica, 宋体; font-size: 12px; white-space: normal; line-height: 2em;\"><span style=\"margin: 0px; padding: 0px; width: 768px; text-indent: 24px; font-family: 微软雅黑, &#39;microsoft yahei&#39;; font-size: 14px;\">当</span><span style=\"margin: 0px; padding: 0px; width: 768px; text-indent: 24px; font-family: 微软雅黑, &#39;microsoft yahei&#39;; font-size: 14px;\">访问网站三级域名时出现“该网站伪装成播放器更新传播木马”或其他危险提示时，</span></p><p style=\"margin-bottom: 0px; margin-left: 24px; padding: 0px; width: 744px; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, arial, sans-serif, Helvetica, 宋体; font-size: 12px; white-space: normal; line-height: 2em;\"><span style=\"margin: 0px; padding: 0px; width: 768px; text-indent: 24px; font-family: 微软雅黑, &#39;microsoft yahei&#39;; font-size: 14px;\">处理方法：三级域名仅仅是用于测试网站程序是否正常，用户访问自己域名时不会出现此类情况；</span></p><p style=\"margin-bottom: 0px; margin-left: 24px; padding: 0px; width: 744px; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, &#39;Hiragino Sans GB&#39;, arial, sans-serif, Helvetica, 宋体; font-size: 12px; white-space: normal; line-height: 2em;\"><span style=\"margin: 0px; padding: 0px; width: 768px; text-indent: 24px; font-family: 宋体; font-size: 14px;\"><span style=\"margin: 0px; padding: 0px; width: 768px; font-family: 微软雅黑, &#39;microsoft yahei&#39;;\">PS</span><span style=\"margin: 0px; padding: 0px; width: 768px; font-family: 微软雅黑, &#39;microsoft yahei&#39;;\">：给用户分配的三级域名过多，某些用户网站被一些安全软件扫描认定为有漏洞、容易被入侵或挂马，导致分配三级域名的根域名被认定为危险域名，其下所有三级域名访问时都会出现安全拦截；</span></span></p><p><br/></p>', '2015-07-02 09:48:37', '1', '0000-00-00 00:00:00');
