DROP TABLE IF EXISTS `agent_users`;
CREATE TABLE `agent_users` (  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,  `username` varchar(60) NOT NULL DEFAULT '' COMMENT '用户名', `password` varchar(64) NOT NULL DEFAULT '' COMMENT '登录密码；mb_password加密', `avatar` varchar(255) NOT NULL DEFAULT '' COMMENT '用户头像，相对于upload/avatar目录',  `email` varchar(100) NOT NULL DEFAULT '' COMMENT '登录邮箱',  `email_code` varchar(60) DEFAULT NULL COMMENT '激活码',  `phone` bigint(11) unsigned DEFAULT NULL COMMENT '手机号',  `status` tinyint(1) NOT NULL DEFAULT '2' COMMENT '用户状态 0：禁用； 1：正常 ；2：未验证',  `register_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '注册时间',  `last_login_ip` varchar(16) NOT NULL DEFAULT '' COMMENT '最后登录ip',  `last_login_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '最后登录时间', PRIMARY KEY (`id`),  KEY `user_login_key` (`username`) USING BTREE) ENGINE=InnoDB DEFAULT CHARSET=utf8;

